mapper.select:
  	 select comm.id,meme1.mtype mtype1,meme1.nickname nickname1,meme1.account account1,
  	 meme2.mtype mtype2,meme2.nickname nickname2,meme2.account account2,title,home.cover, file, file_type,home.content content1,part,type,comm.content content2,comm.remarks,addtime
  	 from meme_comment comm left join meme_homepage home on comm.homepageid = home.id
  	 left join meme_member meme1 on home.memberid = meme1.memberid
  	 left join meme_member meme2 on comm.memberid = meme2.memberid
      WHERE 1=1

		and  (meme1.nickname LIKE  CONCAT('%', #{member_str1},'%') OR meme1.account LIKE  CONCAT('%', #{member_str1},'%'))


		and  (meme2.nickname LIKE  CONCAT('%', #{member_str2},'%') OR meme2.account LIKE  CONCAT('%', #{member_str2},'%'))


		and  file_type = #{file_type}


		AND date_format(addtime,'%Y-%m-%d %H:%i:%s') >= #{addtime}



		AND date_format(addtime,'%Y-%m-%d %H:%i:%s') <= #{addtime}



		order by ${sort} ${order}
		order by addtime desc

  	limit #{offset},#{limit}

   select count(*)
  	 from meme_comment comm left join meme_homepage home on comm.homepageid = home.id
  	 left join meme_member meme1 on home.memberid = meme1.memberid
  	 left join meme_member meme2 on comm.memberid = meme2.memberid
      WHERE 1=1

		and  (meme1.nickname LIKE  CONCAT('%', #{member_str1},'%') OR meme1.account LIKE  CONCAT('%', #{member_str1},'%'))


		and  (meme2.nickname LIKE  CONCAT('%', #{member_str2},'%') OR meme2.account LIKE  CONCAT('%', #{member_str2},'%'))


		and  file_type = #{file_type}


		AND date_format(addtime,'%Y-%m-%d %H:%i:%s') >= #{addtime}



		AND date_format(addtime,'%Y-%m-%d %H:%i:%s') <= #{addtime}
